mapper.insert:

    SELECT IFNULL(MAX(MENU_ID),0)+1 FROM MENU

INSERT INTO MENU(
	 MENU_ID
	, PRNT_MENU_ID
	, MENU_NM
	, MENU_URL
	, MENU_DEPTH
	, MENU_TYPE_CD
	, MENU_LVL_NUM
	, MENU_EXPL
	, ORD_SEQ
	, DSPLY_YN
	, DEL_YN
	, COR_ID
	, COR_DT
	, MOR_ID
	, MOR_DT
)
VALUES (
	#{menuId}
	, #{prntMenuId}
	, #{menuName}
	, #{menuUrl}
	, #{menuDepth}
	, #{menuTypCd}
	, #{menuLvlNum}
	, #{menuExpl}
	, #{menuOrder}
	, #{displayYn}
	, 'N'
	, #{corId}
	, NOW()
	, #{morId}
	, NOW()
)

mapper.update:
UPDATE MENU
   SET MENU_NM = #{menuName}
	, MENU_URL = #{menuUrl}
	, MENU_DEPTH = #{menuDepth}
	, MENU_TYPE_CD = #{menuTypCd}
	, MENU_LVL_NUM = #{menuLvlNum}
	, MENU_EXPL = #{menuExpl}
	, ORD_SEQ = #{menuOrder}
	, DSPLY_YN = #{displayYn}
	, MOR_ID = #{morId}
	, MOR_DT = NOW()
 WHERE MENU_ID = #{menuId}

mapper.delete:
UPDATE MENU
   SET DEL_YN='Y'
 WHERE MENU_ID=#{menuId}
